// Text Chunker for Meshtastic
// ---------------------------
// Splits a string into chunks of ~197 characters (configurable via global.msgChunkSize)
// Preserves words, handles \n for pre-chunked help text, adds metadata for Meshtastic
// Version: 3.11 20251016

let maxLen = parseInt(global.get("msgChunkSize")) || 197;

let text = msg.payload;
if (typeof text !== "string") {
    node.error("Text Chunker expects msg.payload to be a string", msg);
    return null;
}

// Split text by explicit newlines first (help messages or multi-line RSS)
let lines = text.split("\n").map(l => l.trim()).filter(l => l.length > 0);

// Function to split a long string into word-safe chunks
function splitIntoChunks(str, limit) {
    let chunks = [];
    while (str.length > limit) {
        let idx = str.lastIndexOf(" ", limit);
        if (idx === -1) idx = limit; // no space found, cut at limit
        chunks.push(str.substring(0, idx).trim());
        str = str.substring(idx).trim();
    }
    if (str.length > 0) chunks.push(str);
    return chunks;
}

// Process all lines
let finalChunks = [];
lines.forEach(line => {
    if (line.length <= maxLen) {
        finalChunks.push(line);
    } else {
        finalChunks.push(...splitIntoChunks(line, maxLen));
    }
});

// Send each chunk as a separate message for Meshtastic
finalChunks.forEach((chunk, index) => {
    let chunkMsg = { ...msg };
    chunkMsg.payload = chunk;
    chunkMsg.parts = {
        index: index,
        count: finalChunks.length,
        id: msg._msgid,
        type: chunk.includes("|") ? "help" : "normal"
    };
    node.send(chunkMsg);
});

return null; // original message not sent

// Store the shortened URL globally
global.set('tinyUrl', msg.payload);
node.warn('TinyURL generated: ' + msg.payload);
return null; // no message needed further

// Get the repository URL from global context
let longUrl = global.get('repository') || '';
if (!longUrl) {
    node.error('Repository URL not set!');
    return null;
}

// URL-encode it and push into msg.url for the HTTP Request node
msg.url = 'https://tinyurl.com/api-create.php?url=' + encodeURIComponent(longUrl);
return msg;

// Fetch global email addresses
const emailTo = global.get('emailTo') || '';
const emailFrom = global.get('emailFrom') || '';
const basePattern = global.get('basePattern') ? `${global.get('basePattern')}:` : '';


// Assign to msg
msg.to = emailTo;
msg.from = emailFrom;
msg.topic = '[MeshBop] ' + basePattern + ' ' + msg.topic;

// Send if both addresses are defined
if (msg.to.trim() !== "" && msg.from.trim() !== "") {
    return msg; // Pass on the message
}

// Otherwise, block
node.warn('Unable to send email: "to" or "from" address is missing or empty');
return null;

// Retrieve the global function
let getFormattedTime = global.get('getFormattedTime');

// Call it safely
let currentTime = (typeof getFormattedTime === 'function') ? getFormattedTime() : 'Time unavailable';

// Log result
node.warn("Check time settings - I am using: " + currentTime);

return msg;

/**
 * Build a formatted report of all defined global variables
 * and either email it or send it to the dashboard
 */
const version = global.get('version') || "MeshBop â€“ Unversioned build";
const locale = global.get('locale') || 'en-GB';

// List of keys to redact
const redactedKeys = [
    "apiKey",
    "apikey",
    "key",
    "password",
    "pass",
    "secret",
    "token"
];

// Header
let output = `Global Variable Snapshot Report\n`;
output += `--------------------------------------\n`;
output += `${version}\nGenerated: ${new Date().toLocaleString(locale)}\n\n`;

// Collect and sort all global keys
const keys = global.keys().sort();
output += `Total Variables Found: ${keys.length}\n\n`;

// Determine max key length for alignment
const maxKeyLength = Math.max(...keys.map(k => k.length));

// Helper to create dot leaders
function dotLeader(key, padLength) {
    const dots = '.'.repeat(padLength - key.length + 3); // +3 for spacing
    return dots;
}

// Iterate over all keys
for (let key of keys) {
    try {
        const value = global.get(key);

        // Detect sensitive keys
        const isSensitive = redactedKeys.some(s =>
            key.toLowerCase().includes(s.toLowerCase())
        );

        let formatted;
        if (isSensitive) {
            formatted = '[REDACTED]';
        } else if (typeof value === "object") {
            formatted = JSON.stringify(value, null, 2);
        } else if (typeof value === "function") {
            formatted = '[Function]';
        } else {
            formatted = String(value);
        }

        output += `${key}${dotLeader(key, maxKeyLength)}${formatted}\n`;
    } catch (err) {
        output += `${key}${dotLeader(key, maxKeyLength)}[Error reading value]\n`;
    }
}

// Get the current local time in 24-hour format, truncating to HH:MM (no seconds)
let currentTime = new Date().toLocaleTimeString(global.get('locale') || 'en-GB', {
    hour12: false,
    hour: '2-digit',
    minute: '2-digit'
});

// Get the current UTC time in 24-hour format, truncating to HH:MM (no seconds)
let utcTime = new Date().toUTCString().split(' ')[4].substring(0, 5);  // Truncate to HH:MM

// Footer
output += '\nLocal time: ' + currentTime + ' (' + 
utcTime + ' UTC)\n\nEnd of report.\n';

// Prepare message with email fields
msg.topic = "MeshBop Global Variables Report";
msg.payload = output;
// Route based on msg.dashboard
if (msg.dashboard === true) {
    return [null, msg];  // Send to output 2 (dashboard)
} else {
    return [msg, null];  // Send to output 1 (email)
}

// Globals
const version     = global.get('version') || 'MeshBop - Unversioned build';
const basePattern = global.get('basePattern') || '';
const IPaddr      = (global.get('myIP')  ?? '').toString().trim() || 'N/A';
const NodeRED     = (global.get('myNR') ?? '').toString().trim() || 'N/A';
const OS          = (global.get('myOS') ?? '').toString().trim() || 'N/A';

// Exec returns
const uptime = (msg.payload?.uptime ?? '').toString().trim() || 'N/A';
const disk   = (msg.payload?.disk   ?? '').toString().trim() || 'N/A';
const ram    = (msg.payload?.RAM    ?? '').toString().trim() || 'N/A';
const load   = (msg.payload?.load   ?? '').toString().trim() || 'N/A';

msg.topic = 'MeshBop Status Report';
msg.payload = `MeshBop Status Report:

MeshBop Version: ${version}
Base Pattern: ${basePattern}
IP Address: ${IPaddr}
Uptime: ${uptime}
Free Disk Space: ${disk}
RAM: ${ram}
System Load: ${load}
Node-RED version: ${NodeRED}
OS: ${OS}

Sent automatically by Node-RED.`;

return msg;

// Store the host IP address
global.set('myIP', msg.payload);
node.warn('IP Address: ' + msg.payload);
return null; // no message needed further

// Store the Node-RED version
global.set('myNR', msg.payload);
node.warn('Node-RED version: ' + msg.payload);
return null; // no message needed further

// Store the OS ID
global.set('myOS', msg.payload);
node.warn('OS ID: ' + msg.payload);
return null; // no message needed further

/* --------------------------------------------------------------------------
  Parse input messages and determine whether to act on them.

  Version: 1.2 â€” 20251017

  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  Overview:
  Add new regex definitions at the end of the current list. 
  Each entry must include a help message and the corresponding node output index. 
  Use the **Setup** tab above to adjust the number of function outputs as required.

  Help text is assembled in alphabetical order, with triggered items
  (e.g., /l3k[something]) listed first, followed by inbound messages
  without tag patterns (e.g., "test").

  To avoid false positives, messages without a tag pattern are only matched
  if they are the **first and only element** in a message. 
  For example:
    "test" â†’ will trigger a response
    "This is a test" â†’ will NOT trigger

  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  Regex pattern structure:
  Each entry follows the general form:
      pattern, "help message text", output index

  Example:
      { regex: /^\/?ping$/, helpText: "ping = Ping test", outputIndex: 3 }

  â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  Adding new regex triggers:
  â€¢ Copy one of the existing definitions.
  â€¢ Modify the pattern, help text, and output index as needed.
  â€¢ Place the new entry where indicated below in the list.

  Important:
  Thoroughly test any new trigger.
  Do not create patterns that could respond to common message content.

  -------------------------------------------------------------------------- */

if (!msg || msg.payload == null) return null;

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// Setup and context
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const basePattern = (global.get("basePattern") || "/l3k").toLowerCase();
const helpTrigger = global.get("helpTrigger");
const ident = basePattern.replace(/^\/(.*)$/, '$1');
const version = global.get('version') || '';

const content = String(msg.payload || "")
    .replace(/^[^:]+:\s*/, '')     // Remove sender prefix if present
    .trim()
    .replace(/[^\x20-\x7E]/g, '')  // Strip non-printable chars
    .toLowerCase();

// Debug output (optional)
node.warn(`basePattern="${basePattern}", content="${content}"`);

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// Command patterns
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const patterns = [
    { regex: /^\/?(test|testing)$/, helpText: `test = Test message (also /test).`, outputIndex: 2 },
    { regex: /^\/?ping$/, helpText: `ping = Ping test (also /ping).`, outputIndex: 3 },
    { regex: new RegExp(`^${basePattern}w$`), helpText: `${basePattern}w = Weather.`, outputIndex: 4 },
    {
        regex: new RegExp(`^${basePattern}n(?:,(?:\\d+|[a-z]+|u))*\\??$`),
        helpText: `${basePattern}n = Get news headlines. Optional: ,num for # articles (max 10), ,feed for source (bbc, nyt, cnn, etc.), ,u for URLs. Send '${basePattern}n?' to see a list of available news feeds.`,
        outputIndex: 5
    },
    { regex: new RegExp(`^${basePattern}t$`), helpText: `${basePattern}t = Time & astro info.`, outputIndex: 6 },
    { regex: new RegExp(`^${basePattern}z$`), helpText: `${basePattern}z = Zen quote.`, outputIndex: 7 },
    { regex: new RegExp(`^${basePattern}i$`), helpText: `${basePattern}i = App info and disclaimers.`, outputIndex: 8 },
    { regex: new RegExp(`@${ident}`), helpText: `@${ident} = Send email to admin.`, outputIndex: 9 },
    { regex: new RegExp(`^${basePattern}s$`), helpText: `${basePattern}s = Sea state report.`, outputIndex: 10 },
    { regex: new RegExp(`^${basePattern}ws$`), helpText: `${basePattern}ws = Check for severe weather report.`, outputIndex: 11 }

    // Add new regexes below here.
];

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// Help pattern setup
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
let helpPattern = null;
if (helpTrigger) {
    const escaped = helpTrigger.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&');
    helpPattern = {
        regex: new RegExp(`^${basePattern}${escaped}$`),
        helpText: `${basePattern}${helpTrigger} = Help menu.`,
        outputIndex: 1
    };
    patterns.push(helpPattern);
}

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// Help builder
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function assembleHelpBlocks(maxLen = 197) {
    if (!helpTrigger) return null;
    const helpMessages = patterns
        .filter(p => p !== helpPattern)
        .map(p => p.helpText);

    // Order: command patterns first, generic triggers after
    const ordered = [
        ...helpMessages.filter(m => m.startsWith('/')).sort(),
        ...helpMessages.filter(m => !m.startsWith('/')).sort()
    ];

    const blocks = [];
    let current = version ? `${version} ` : 'Help menu: ';

    for (const msg of ordered) {
        const cleaned = msg.replace(/\n/g, ' ').trim();
        if ((current + cleaned + ' | ').length > maxLen) {
            blocks.push({ payload: current.replace(/\s*\|\s*$/, ''), type: 'noident' });
            current = '';
        }
        current += cleaned + ' | ';
    }

    if (current.trim()) {
        blocks.push({ payload: current.replace(/\s*\|\s*$/, ''), type: 'noident' });
    }

    return blocks;
}

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// Output handling
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const outputCount = 11;  // 0â€“10 inclusive
const emptyOutputs = () => Array(outputCount).fill(null);

// Manual help request
if (msg.topic === "generate_help") {
    const out = emptyOutputs();
    out[0] = { payload: "Help requested" };
    out[1] = assembleHelpBlocks();
    return out;
}

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// Pattern matching and routing
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
for (const pattern of patterns) {
    if (pattern.regex.test(content)) {
        const out = emptyOutputs();
        out[0] = { payload: `Matched: "${content}" â†’ ${pattern.helpText}` };
        node.warn(`Routing to outputIndex=${pattern.outputIndex}`);

        if (pattern === helpPattern) {
            out[1] = assembleHelpBlocks();
        } else {
            out[pattern.outputIndex] = msg;
        }

        return out;
    }
}

// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// No match found
// â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const out = emptyOutputs();
out[0] = { payload: `No match found for: "${content}"` };
return out;

// TxMessage: version + no guarantees message
let TxMessage = {
    payload: (global.get('version') || '') + ' ' + (global.get('emailResponse') || ''),
    messageType: 'TxMessage'
};

// emailMessage: pass inbound msg.payload and msg.topic unchanged
let emailMessage = msg;

// Return as two outputs: [TxMessage, emailMessage]
return [TxMessage, emailMessage];

// Check if 'noBase' is set (can come from msg or global)
let noBase = msg.noBase || global.get('noBase');

// Default base pattern
let base = global.get('basePattern') || "/T$BOP";

// If noBase is true, remove the base prefix
if (noBase === true) {
    base = '';
}

// Build final payload
msg.payload = 'TEST:' + `${base}${String(msg.payload)}`.trim();

return msg;

// Removes problematic characters in string
// V1.3 20251007

let str = msg.payload;

// Remove any leading or trailing spaces in the received message
str = str.trim();

// Remove ASCII control characters except BEL (\x07)
str = str.replace(/[\x00-\x06\x08-\x1F\x7F]/g, '');  // Remove unwanted control chars

// Skip empty messages after cleaning
if (str === '') return null;

msg.payload = str;
return msg;

// V1.1 20251014

if (global.get('version') && global.get('helpTrigger') && global.get('basePattern')) {
    msg.payload = global.get('version') + ' Send ' + global.get('basePattern') 
      + global.get('helpTrigger') + ' for menu.';
    return msg;
} else {
    return null; // Don't send anything if any value is missing or empty
}

// Remove ASCII control characters (excelt BEL)
//
// V1.1 20251007

let message = msg.payload;

// Split and filter out empty sections
let sections = message.split('\n').filter(s => s.trim() !== '');

// Regular expression to remove ASCII control characters except BEL (\x07)
let controlCharRegex = /[\x00-\x06\x08-\x1F\x7F]/g;

sections.forEach(section => {
    // Clean the section: remove unwanted control characters
    let cleanedSection = section.replace(controlCharRegex, '');

    if (cleanedSection === '') return; // skip empty sections

    // Send the cleaned section as a message
    let newMsg = { ...msg };
    newMsg.payload = cleanedSection + '\n'; // keep \n at end
    node.send([newMsg, null]);
});

// Send completion message if applicable
if (msg.type !== 'noident') {
    let completionMsg = { ...msg };
    completionMsg.payload = true;
    node.send([null, completionMsg]);
}

// Prevent the original message from being sent
return null;

// Detect test messages
const isTest =
    msg.test === true ||
    msg.topic === "test" ||
    msg.payload?.type === "test" ||
    msg.payload?.test === true;

// Log messages
const logPrefix = isTest ? "[TEST DROPPED]" : "[OUTBOUND]";
const logBody = `${msg.topic ? msg.topic + " " : ""}${JSON.stringify(msg.payload)}`;
node.warn(`${logPrefix} ${logBody}`);

// If test, send reset signal with empty payload.
// This is used to reset the rate limiters during
// testing.
if (isTest) {
    let resetMsg = { reset: true }; // payload intentionally left empty
    return [resetMsg, null];
}

// Otherwise, pass normal messages through second output
return [null, msg];

node.warn('Info string = ' + global.get('info'));

msg.payload = global.get('info') || 'MeshBop messaging service is online.'; // Default
msg.infoText = msg.payload; // Preserve for later combination
return msg;

let hT = global.get('basePattern') +
              global.get('helpTrigger');

msg.payload = (msg.infoText || global.get('info') || 'MeshBop messaging service is online.')
             .replace(/\n+$/,'')  // remove trailing newlines
             + '\nMeshBop menu - send ' + hT;

msg.type = 'noident';
return msg;

// Ensure version is set
global.get("version") || "Version not set";

// Check and normalize inbound payload
if (typeof msg.payload !== "string" || !msg.payload.includes(":")) {
    msg.payload = "UNDEF:ping";
}

// Split incoming payload
let parts = msg.payload.split(":");
let location = global.get('location') || 'London';

// Use the global time function
let getFormattedTime = global.get('getFormattedTime');
let currentTime = (typeof getFormattedTime === 'function') ? getFormattedTime() : 'Time unavailable';

// Assemble the message with version appended
msg.payload = `Hello ${parts[0].trim()}. ${parts[1].trim().charAt(0).toUpperCase() 
  + parts[1].trim().slice(1).toLowerCase()
} received in ${location} at ${currentTime} by ${global.get('version')}`;

return msg;

// Ensure the data is available in msg (sunRise and sunSet)
let sunrise = msg.sunRise;
let sunset = msg.sunSet;

// Truncate sunrise and sunset to just HH:MM (remove the seconds)
sunrise = sunrise.substring(0, 5);  // Extracts the first 5 characters (HH:MM)
sunset = sunset.substring(0, 5);    // Same for sunset

// Get the current local time in 24-hour format, truncating to HH:MM (no seconds)
let currentTime = new Date().toLocaleTimeString(global.get('locale') || 'en-GB', {
    hour12: false,
    hour: '2-digit',
    minute: '2-digit'
});

// Get the current UTC time in 24-hour format, truncating to HH:MM (no seconds)
let utcTime = new Date().toUTCString().split(' ')[4].substring(0, 5);  // Truncate to HH:MM

// Construct the message
msg.payload = (global.get('location') || 'London') +
    ': Local time ' + currentTime +
    ' (' + utcTime + ' UTC)' +
    '. Sunrise: ' + sunrise +
    ', Sunset: ' + sunset + ' (local times).';

return msg;

// Retrieve global variables to be used in the call to astrodata
msg.lat = global.get('latitude') || 51.5074;
msg.lon = global.get('longitude') || -0.1278;
msg.height = global.get('height') || 10;

return msg;

// --------------------
// Limit number of RSS articles and use title field
// --------------------

// Retrieve the processed count from flow context (initialize to 0 if not set)
let count = flow.get('processedCount') || 0; 
let numItems = flow.get('numItems') || 5;

// Process the message only if we haven't processed the required number of items yet
if (count < numItems) {
    count += 1;  // Increment the processed count

    // Use the article.title field if available, fallback to msg.payload
    let messageContent = msg.article?.title || msg.payload || "";

    // Truncate the message if it's longer than 198 characters
    if (messageContent.length > 198) {
        messageContent = messageContent.slice(0, 198);  
    }

    // Save the truncated text in msg.article for downstream nodes
    msg.article = messageContent;

    // Preserve for later use (e.g., TinyURL concatenation)
    msg.infoText = messageContent;

