msg.url = `https://admiraltyapi.azure-api.net/uktidalapi/api/V1/Stations/${tideStationId}/TidalEvents`;
msg.headers = { 'Ocp-Apim-Subscription-Key': global.get('admiraltyApiKey') || '' };
msg.TideInfo = null;
return msg;

// Tide processing function: all tide events for today, UTC + local times
let locale = global.get('locale') || 'en-GB';
let tideLoc = global.get('tideLoc') || 'Unknown location';

if (msg.payload && Array.isArray(msg.payload) && msg.payload.length > 0) {
    let now = new Date();
    let today = now.toISOString().split('T')[0]; // YYYY-MM-DD

    // Filter only tide events for today
    let todaysTides = msg.payload.filter(p => p.Date.startsWith(today));

    if (todaysTides.length === 0) {
        msg.TideInfo = `(No tide data available for today at ${tideLoc})`;
    } else {
        // Sort by DateTime ascending
        todaysTides.sort((a, b) => new Date(a.DateTime).getTime() - new Date(b.DateTime).getTime());

        // Build concise strings for each event
        let tideStrings = todaysTides.map(p => {
            let utcTime = new Date(p.DateTime).toLocaleTimeString(locale, { hour: '2-digit', minute: '2-digit', timeZone: 'UTC' });
            let localTime = new Date(p.DateTime).toLocaleTimeString(locale, { hour: '2-digit', minute: '2-digit', timeZone: global.get('timezone') || 'Europe/London' });
            let height = p.Height.toFixed(2) + 'm';
            return `${p.EventType.replace('Water', '')} ${utcTime} (${localTime} local) ${height}`;
        });

        msg.TideInfo = `${tideLoc}: ` + tideStrings.join(', ');
    }
} else {
    msg.TideInfo = `(Tide info unavailable for ${tideLoc})`;
}

return msg;

let seaPart = msg.SeaInfo || '(Sea info unavailable)';
let tidePart = msg.TideInfo + '. Not for navigation.' || '(Tide info unavailable)';
msg.payload = `${tidePart}\n${seaPart}`;
return msg;

msg.requestTimeout = 10000;  // 10 seconds

// User-editable search term
let searchTerm = msg.searchTerm || 'Littlehampton';
let apiKey = msg.apiKey || global.get('admiraltyApiKey'); // fallback to global

// Check if API key is set
if (!apiKey) {
    msg.payload = `Error: Admiralty API key not set. Please configure global 'admiraltyApiKey' or provide msg.apiKey.`;
