msg.headers = { 'Ocp-Apim-Subscription-Key': apiKey };

return msg;

// Get search term (default to empty string if not provided)
let searchTerm = (msg.searchTerm || "").toLowerCase();

// Check payload contains features
if (!msg.payload || !msg.payload.features) {
