    return null;
}

// Find a matching station name
let station = msg.payload.features.find(f =>
    f.properties.Name && f.properties.Name.toLowerCase().includes(searchTerm)
);

let resultText = "";

if (station) {
    resultText = `Search text: "${msg.searchTerm}". Result: ${station.properties.Name} (ID: ${station.properties.Id})`;
} else {
    resultText = `Search text: "${msg.searchTerm}". No matching tidal station found.`;
}

// Output 1 â†’ full API results
// Output 2 â†’ summary string
return [msg, { payload: resultText }];

if (typeof msg.payload === 'string') {
    // Remove non-ASCII characters
    msg.payload = msg.payload.replace(/[^\x00-\x7F]/g, '');
    // Limit length to 198 characters
    // Not needed in Beta 0.10 onwards msg.payload = msg.payload.slice(0, 198);
    // Trim whitespace and drop if empty
    if (msg.payload.trim() === '') {
        return null; // message is empty, do not send
    }
}

// This stops the ident message being sent after every message we type
msg.type = 'noident';

// Get the current local time in 24-hour format
let currentTime = new Date().toLocaleTimeString(global.get('locale') || 'en-GB', { hour12: false });

// Format the timestamp as DD-MMM HH:MM
let now = new Date();
let day = String(now.getDate()).padStart(2, '0');
let monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
    "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
let month = monthNames[now.getMonth()];

// Extract HH:MM from currentTime (HH:MM:SS)
let hhmm = currentTime.split(':').slice(0, 2).join(':');

// Create second message with timestamp
let msgWithTimestamp = { ...msg };
msgWithTimestamp.payload = `~MBP~${day}-${month} ${hhmm}: ${msg.payload}`;

// Return both outputs: first is original, second is with timestamp
return [msg, msgWithTimestamp];

// Helper function to generate a random string of given length
function randomWord(length) {
    const chars = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
    let result = '';
    for (let i = 0; i < length; i++) {
        result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
}

// Generate random number of 'words' (between 3 and 7)
const numWords = Math.floor(Math.random() * (7 - 3 + 1)) + 3;

// Generate each 'word' with random length (3 to 7 characters)
let words = [];
for (let i = 0; i < numWords; i++) {
    const wordLength = Math.floor(Math.random() * (7 - 3 + 1)) + 3;
    words.push(randomWord(wordLength));
}

// Combine 'words' with spaces and prepend "AAAA: to make a test message
msg.payload = `AAAA: ${words.join(' ')}`;

return msg;

// Accumulate log lines in node context
let log = context.get('log') || [];

// Check for reset
if (msg.reset === true) {
    log = [];  // clear the log
} else if (msg.payload !== undefined) {
    let line = msg.payload;
    log.push(line);

    // Keep last 50 lines (change this to adjust total buffered lines)
    if (log.length > 75) log.shift();
}

// store updated log in context
context.set('log', log);

// send the entire array to the template
return { payload: log };

// Get the current local time in 24-hour format
let currentTime = new Date().toLocaleTimeString(global.get('locale') || 'en-GB', { hour12: false });
// Extract HH:MM
let hhmm = currentTime.split(':').slice(0, 2).join(':');

// Get today's date
const now = new Date();
const day = String(now.getDate()).padStart(2, '0');
const monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun",
    "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
const month = monthNames[now.getMonth()];
const year = now.getFullYear();

// Ensure payload is a string, trim it, and remove non-ASCII characters
let messageText = "";
if (typeof msg.payload === "string") {
    messageText = msg.payload.trim().replace(/[^\x20-\x7E]/g, '');
}

// Construct final message: date + time + space + full original payload
//msg.payload = `${day}-${month}-${year} ${hhmm} ${messageText}`;
msg.payload = `${day}-${month} ${hhmm}: ${messageText}`;

return msg;

// Initialize outputs
let liveCountMsg = null;
let submitMsg = null;

// Get stored text or empty
let currentText = flow.get("currentText") || "";

// Handle text input (string payload)
if (typeof msg.payload === "string") {
    currentText = msg.payload || "";
    liveCountMsg = { payload: currentText.length }; // output 1 â†’ live char count
    flow.set("currentText", currentText);
}

// Handle send button (boolean true)
else if (msg.payload === true && msg.topic !== "initialise") {
    if (currentText.length > 0) {                    // only send if not empty
        submitMsg = { payload: currentText };       // output 2 â†’ final string
        currentText = "";                            // clear stored string
        flow.set("currentText", currentText);
        liveCountMsg = { payload: 0 };              // reset counter display
    }
}

// Handle initialise trigger
else if (msg.topic === "initialise" && msg.payload === true) {
    currentText = "";                                // clear stored string
    flow.set("currentText", currentText);
    liveCountMsg = { payload: 0 };                  // reset counter display
}

// Return outputs: [live count, final submission]
return [liveCountMsg, submitMsg];

// Get colors from global context
const colorLocal = global.get("MBPColourLocal") || "#000000";
const colorRx = global.get("MBPColourRx") || "#303030";

// Add colors to the message
msg.colorLocal = colorLocal;
msg.colorRx = colorRx;

return msg;

// Get coordinates from global context, fallback to centre of London
let lat = global.get('latitude') || 51.5074;
let lon = global.get('longitude') || -0.1278;

// Set the URL with additional parameters
msg.url = `https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lon}&current=temperature_2m,apparent_temperature,weather_code,wind_speed_10m,wind_direction_10m,pressure_msl&timezone=Europe/London`;
return msg;

// Debug: Check what we're receiving
